mapper.select:
SELECT count(*)
FROM login
WHERE id = #{id}
	AND password = #{password}

SELECT *
FROM login
WHERE id = #{id}
	AND password = #{password}

mapper.update:
UPDATE employ 
SET m_id = #{m_id}
WHERE e_id=#{e_id}

mapper.insert:
INSERT INTO tag(content, e_id)
SELECT mt.content, e.e_id FROM mbti_tag mt , employ e 
WHERE e.e_id = #{e_id} and mt.m_id = e.m_id;
